import {Routes, Route, Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from "react";

import Navbar from './components/navbar.component';
import CreateUser from './components/create-user.component';
import Login from './components/login';

import Navbar2 from './components/navbar.component'
// import Button from '../node_modules/react-bootstrap/esm/Button';
// import Button from '../node_modules/@restart/ui/esm/Button';
// import { Button } from '../node_modules/reactstrap/types/index';
// import { Button } from '../node_modules/bootstrap/dist/js/bootstrap';
import { Button } from 'bootstrap';

function App() {

  const[username,setUsername] =  React.useState('');
  const[password,setPassword] =  React.useState('');
  let[client,setClient] = React.userState('');
  [employee,setEmployee] = React.userState('');
  [admin,setAdmin] = React.userState('');


async function registerUser(event){
  event.preventDefault();

    const response = await fetch('http://localhost:5000/users/add',{
      method: 'POST',
      headers:{
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password
      }),
    })
    const data = await response.json()
    console.log(data)
}


  return (
    


    <div>
      <h1>Register</h1>
      <form onSubmit={registerUser}>
        <br/>
        <input value={username} 
        onChange={(e) => setUsername(e.target.value)}
        type="text" placeholder='username'/>
                <br/>

        <input
        value={password} 
        onChange={(e) => setPassword(e.target.value)}
        type="text" placeholder='password'/>
        <input
        value="Client"
        onChange={(e) => setClient(e.target.value)}
        type="radio"/>
      <input type="submit" value= "Register"/>

      </form>
    </div>
    

  );
}

export default App;
