import 'bootstrap/dist/css/bootstrap.min.css';
import React from "react";


function App() {

  const[username,setUsername] =  React.useState('');
  const[password,setPassword] =  React.useState('');


async function loginUser(event){
  event.preventDefault();

    const response = await fetch('http://localhost:5000/users/find',{
      method: 'POST',
      headers:{
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username,
        password
      }),
    })
    const data = await response.json()

    if(data.user){
        localStorage.setItem('token',data.user)
        alert('Login Successful')
        window.location.href = '/'
    }
    else{
        alert('Invalid Username or password')
    }

}


  return (
    


    <div>

      <form onSubmit={loginUser} class="form-horizontal">
      <div class="form-group">
    <label class="control-label col-sm-2" for="email">UserName:</label>
    <div class="col-sm-10">

    <input class="form-control" id="usernameid"
    input value={username} 
    onChange={(e) => setUsername(e.target.value)}
    type="text" placeholder='username'/>
    </div>
  </div>
  <div  class="form-group">
    <label label class="control-label col-sm-2" for="pwd">Password:</label>
    <input value={password} 
        onChange={(e) => setPassword(e.target.value)}
        type="text" placeholder='password' class="form-control" id="pwd"/>
  </div>
  <div class="form-group">
    <div class="col-sm-offset-2 col-sm-10">
      <button type="submit" class="btn btn-default">Submit</button>
    </div>
  </div>


      </form>



    </div>
    

  

  );
}

export default App;
