import {Routes, Route, Link} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import React from "react";
import jwtDecode from 'jwt-decode';
import * as ReactBootStrap from "react-bootstrap";
import Bookcomp from 'Bookcomp.js';

function Books() {

  const [userid,setUserid] = React.useState("");
  const [bookslist, setBooks] = React.useState([]);

  React.useEffect(() => {
    userss();
    if(userid){
      returnBookss();
    }
  },[userid]);

  
 async function userss(){
    const token = localStorage.getItem('token')
    const newuser = jwtDecode(token);
    console.log(newuser);
    setUserid(newuser.id);
    console.log(userid);


  }
  
  async function returnBookss(){
    console.log("HEllo")

      
      const response = await fetch('http://localhost:5000/Books/all',{
        method: 'POST',
        headers:{
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userid
        }),
      })
      const data = await response.json()
      if(data){
          
          await setBooks(data);
          console.log(data)
      }
      else{
          console.log("no response")
      }
  
    
  }


  



  return (
    
    <div>

        <Table striped bordered hover>
  <thead>
    <tr>
      <th>#</th>
      <th>First Name</th>
      <th>Last Name</th>
      <th>Username</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan={2}>Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</Table>

    </div>

  )

}

export default Books;
