        },[bookid]);

            // async function onLoad(){
            // await setBookId(props.book.id);
            // console.log("bookid:   " +bookid)

            // }


      const makeTooExpensive = (bookid) =>{
    console.log("Book id= "  +bookid )
    makeTooExpensivee(bookid)
    
  }

    async function makeTooExpensivee(bookid){

      console.log("HEllo")

      
      const response = await fetch('http://localhost:5000/Books/change',{
        method: 'POST',
        headers:{
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          bookid
        }),
      })
      const data = await response.json()
      if(data){
          
          console.log(data)
      }
      else{
          console.log("no response")
      }
  

    }




    return (
        <div>
            <h1>
            {"Title:  " + props.book.bookName + "   Cost:  " + props.book.cost  + "    Autherised: " + props.book.auth }
            
            </h1>
            {props.rank == "Admin" ? (
               <div>
                   <h1>{"Is too expensive  " + props.book.toExpensive}

                   </h1>
          <button >Aprove Purchase</button>
          <button >Deney Purchse</button>
          </div>
          
      ) : (
        <h1></h1>
      )}
      {props.rank == "Employee" ? (
               <div>
          <button >Yes</button>
          <button >Suspend Request</button>
          <button onClick={() => makeTooExpensive(props.book.id)}>Too exensize ask admin lol</button>

          </div>
          
      ) : (
        <h1></h1>
      )}
      {props.rank == "Client" ? (
               <div>
          </div>
          
      ) : (
        <h1></h1>
      )}

        </div>
    );
}
export default Bookcomp;
